import React from 'react'
import re2 from "./component/Basics/images/re2.png"
import re3 from "./component/Basics/images/re3.png"
import re4 from "./component/Basics/images/re4.png"
import re5 from "./component/Basics/images/re5.png"
import re6 from "./component/Basics/images/re6.png"
import re7 from "./component/Basics/images/re7.png"
import { useState } from 'react';

const App = ({title}) => {



  const [blogs, setBlogs] = useState([
    { title: <img src={images/re2} />, body: 'India, officially the Republic of India (Hindi: Bhārat Gaṇarājya), is a country in South Asia. It is the seventh-largest country by area, the second-most populous country, and the most populous democracy in the world. Bounded by the Indian Ocean on the south, the Arabian Sea on the southwest, and the Bay of Bengal on the southeast, it shares land borders with Pakistan to the west;[f] China, Nepal, and Bhutan to the north; and Bangladesh and Myanmar to the east.', author: 'Jagrit', id: 1 },
    { title: <img src={images/re3} />, body: 'Afghanistan officially the Islamic Emirate of Afghanistan, is a landlocked country at the crossroads of Central and South Asia. It is bordered by Pakistan to the east and south, Iran to the west, Turkmenistan and Uzbekistan to the north, and Tajikistan and China to the northeast. Occupying 652,864 square kilometers (252,072 sq mi), the country is predominately mountainous with plains in the north and southwest.', author: 'Jagrit', id: 2 },
    { title: <img src={images/re4} />, body: 'Argentina officially the Argentine Republic is a country in the southern half of South America. It shares the bulk of the Southern Cone with Chile to the west, and is also bordered by Bolivia and Paraguay to the north, Brazil to the northeast, Uruguay and the South Atlantic Ocean to the east, and the Drake Passage to the south. Argentina covers an area of 2,780,400 km2 (1,073,500 sq mi), making it the largest Spanish-speaking nation in the world.', author: 'Jagrit', id: 3 },
    { title: <img src={images/re5} />, body: 'England is a country that is part of the United Kingdom. It shares land borders with Wales to its west and Scotland to its north. The Irish Sea lies northwest of England and the Celtic Sea to the southwest. England is separated from continental Europe by the North Sea to the east and the English Channel to the south. The country covers five-eighths of the island of Great Britain, which lies in the North Atlantic, and includes over 100 smaller islands, such as the Isles of Scilly and the Isle of Wight.', author: 'Jagrit', id: 4 },
    { title: <img src={images/re6} />, body: 'The United States of America (U.S.A. or USA), commonly known as the United States (U.S. or US) or America, is a country primarily located in North America. It consists of 50 states, a federal district, five major unincorporated territories, 326 Indian reservations, and some minor possessions. At 3.8 million square miles (9.8 million square kilometers), it is the worlds  third- or fourth-largest country by total area.', author: 'Jagrit', id: 5 },
    { title: <img src={images/re7} />, body: 'Australia, officially the Commonwealth of Australia, is a sovereign country comprising the mainland of the Australian continent, the island of Tasmania, and numerous smaller islands. It is the largest country by area in Oceania and the worlds sixth-largest country. Australias population of nearly 26 million, in an area of 7,617,930 square kilometres (2,941,300 sq mi), is highly urbanised and heavily concentrated on the eastern seaboard.', author: 'Jagrit', id: 6 },

  ]);



  const handleDelete = (id) => {
    const newBlogs = blogs.filter(blogs => blogs.id !== id);
    setBlogs(newBlogs);
  }


  return (
    <div className="home1">
      <h2>{blogs.title}</h2>
      {blogs.map((blog) => (
        <div className="blog-preview" key={blogs.id}>
          <h2>{blogs.title}</h2>
          <p className="sec1"> Written by {blogs.author}</p>
          <p>About- {blogs.body}</p>
          <button onClick={() => handleDelete(blogs.id)}> Delete blog</button>
        </div>

      ))}

    </div>
  );
}

export default App
